import React from 'react';
import './App.css';

import UserCard from "./components/UserCard"

class App extends React.Component {
  state = {
    gitHubUsers: []
  };

  componentDidMount() {
    fetch("https://api.github.com/survani/")
  }
  render(){
    return (
      <div>
        {this.state.user.map( user => (
        <UserCard
        key={user.id}
        name={user.name}
        bio={user.bio}
        img={user.avatar_url}
        />
        ))}
      </div>
    )
  }
}

export default App;
