import React from 'react';
import './App.css';

import UserCard from "./components/UserCard"

class App extends React.Component {
  state = {
    gitHubUsers: []
  };

  componentDidMount() {
    fetch("https://api.github.com/users/survani")
    .then(response => response.json())
    .then(usersData => {
      //no need to have usersData.data like I had it before
      // because the data returning does not have that.
      this.setState({...this.state, gitHubUsers: usersData })
      console.log("gg: Data:", usersData )
    })
    .catch(error => console.log("Got an error on fetch: ", error))
  }
  render(){
    return (
      <div>
        <h1>Github User</h1>
        {this.state.doggos && 
        this.state.gitHubUsers.map( users => (
        <UserCard
        key={users.id}
        name={users.name}
        bio={users.bio}
        img={users.avatar_url}
        />
        ))}
      </div>
    )
  }
}

export default App;
