import React from 'react';
import './App.css';

import UserCard from "./components/UserCard"

class App extends React.Component {
  state = {
    gitHubUsers: []
  };

  componentDidMount() {
    fetch("https://api.github.com/users/survani")
    .then(response => response.json())
    .then(users => {
      this.setState({...this.state, gitHubUsers: users.all })
    })
    .catch(error => console.log("Got an error on fetch: ", error))
  }
  render(){
    return (
      <div>
        {this.state.gitHubUsers.map( user => (
        <UserCard
        key={user.id}
        name={user.name}
        bio={user.bio}
        img={user.avatar_url}
        />
        ))}
      </div>
    )
  }
}

export default App;
